import React, { useState } from 'react';
import { Form, Icon, Input, Button, DatePicker, TimePicker } from 'antd';
import moment from 'moment';



const CustomForm = (props) => {
    const {addReserve} = props;
    const { getFieldDecorator } = props.form;
    const  [date, setDate] = useState(null);
    const [startTime, setStartTime] = useState(null);
    const [endTime, setEndTime] = useState(null);

const handleSubmit = event => {
    event.preventDefault();
    props.form.validateFields((err, values) => {
          if (!err) {
              const reserve = {...values, date, startTime, endTime};
                console.log('Received values of form: ', reserve);
                addReserve(reserve);
          }
        });
      };

const onDateChange = (date, dateString) => {
    console.log(date, dateString);
    setDate(date);
};

const onStartTimeChange = (time, timeString) => {
    console.log(time, timeString)
    setStartTime(timeString);
};

const onEndTimeChange = (time, timeString) => {
    console.log(time, timeString)
    setEndTime(timeString);
};

const format = 'HH:mm';

    return (
        <Form layout="inline" onSubmit={handleSubmit}>

            <Form.Item>
                {getFieldDecorator('name', {
                rules: [{ required: true, message: '¡Por favor, ingrese su nombre.' }],
                })
                ( <Input placeholder='Nombre'/> )}
            </Form.Item>

            <Form.Item>
                <DatePicker onChange={onDateChange}/>
            </Form.Item>

            <Form.Item>
                <TimePicker onChange={onStartTimeChange} format={format}/>
            </Form.Item>

            <Form.Item>
                <TimePicker onChange={onEndTimeChange} format={format}/>
            </Form.Item>

            <Form.Item>
                <Button type="primary" htmlType="submit">
                Guardar
                </Button>
            </Form.Item>

        </Form>
    );
};


export default Form.create({ name: 'reservation_form' })(CustomForm)
