import React from 'react';
import { Table, Icon, Popconfirm } from 'antd';

const CustomTable = (props) => {
    const {data} = props;
    const confirm = () => {
      console.log('eliminar')
    }
    const columns = [
      {
        title: 'Nombre',
        dataIndex: 'name',
      },
      {
        title: 'Desde',
        dataIndex: 'startTime',
      },
      {
        title: 'Hasta',
        dataIndex: 'endTime',
      },
      {
        title: 'Borrar',
        render: () => (
          <Popconfirm
            title="Are you sure delete this task?"
            onConfirm={confirm}
            okText="Yes"
            cancelText="No">
              <Icon type='delete' />
          </Popconfirm>
        )
      }
    ];
    return (
      <Table columns={columns} dataSource={data} pagination={false}/>
    );
};

export default CustomTable;
